import './style.css';
import { useState } from 'react';

const Account = () => {
  const [settingsTab, setSettingsTab] = useState(false);

  const changeTab = () => setSettingsTab(!settingsTab);

  return (
    <div>
      <div className="tab-switch">
        <label className="switch">
          <input
            type="checkbox"
            id="tab-switch-checkbox"
            checked={settingsTab}
            onChange={changeTab}
          />

          <span className="slider"></span>
          <span className="switch-option first-option">My Items</span>
          <span className="switch-option second-option">Settings</span>
        </label>
      </div>
      <div className="tab-container">
        {!settingsTab && <div className="my-items-tab">My Items</div>}
        {settingsTab && <div className="settings-tab">Settings</div>}
      </div>
    </div>
  );
};

export default Account;
