import './style.css';
import { Link, Navigate } from 'react-router-dom';
import { useState } from 'react';

const LoginForm = () => {
  if (this.props.isAuthenticated) {
    return <Navigate to="/account" />;
  }

  const initialValues = {
    username: '',
    password: '',
  };

  const [state, setState] = useState(initialValues);

  const onChange = (e) => setState({ [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    this.props.login(state.username, state.password);
  };

  return (
    <div className="login-outer-wrapper">
      <div className="login-wrapper">
        <div className="login-header">Login</div>
        <form className="login-form" onSubmit={onSubmit}>
          <div className="form-field">
            <label htmlFor="username">Login</label>
            <input
              type="text"
              id="username"
              name="username"
              onChange={onChange}
              className="login-form-input"
              value={state.username}
              required
            />
          </div>
          <div className="form-field">
            <div className="password-grid">
              <label htmlFor="password">Hasło</label>
              <div className="reset-password">
                <Link to="/forgottenPassword" className="reset-password-link">
                  Zapomniałeś hasła?
                </Link>
              </div>
            </div>
            <input
              type="password"
              id="password"
              name="password"
              className="login-form-input"
              onChange={onChange}
              value={state.password}
              required
            />
          </div>
          <div className="form-field submit-field">
            <input
              type="submit"
              value="Zaloguj"
              className="login-register-submit"
            />
          </div>
        </form>
      </div>
      <div className="login-register-redirect">
        Nie masz jeszcze konta? <Link to="/register">Zarejestruj się</Link>
      </div>
    </div>
  );
};

export default LoginForm;
