import './style.css';
import { useState } from 'react';
import Button from '../../../common/Button';
import InputField from '../../../common/form-fields/InputField';
import ItemOptions from '../ItemOptions';

const ScrapedData = () => {
  const initialValues = {
    name: 'Sukienka top-maxi',
    shop: 'Mango',
    price: '149',
    currency: 'PLN',
    expectedPrice: '',
    tracked: false,
  };

  const [state, setState] = useState(initialValues);

  const onChange = (e) =>
    setState({ ...state, [e.target.name]: e.target.value });

  const onCheckboxChange = (e) =>
    setState({ ...state, [e.target.name]: e.target.checked });

  const addItemToMyList = (e) => console.log('add item to list');

  return (
    <div className="scrapped-data-wrapper">
      <div className="scrapped-data">
        <div className="item-data">
          <div className="sd-item sd-name">
            <div className="sd-label">Name</div>
            <div className="sd-value">{state.name}</div>
          </div>
          <div className="sd-item sd-shop">
            <div className="sd-label">Shop</div>
            <div className="sd-value">{state.shop}</div>
          </div>
          <div className="sd-item sd-price">
            <div className="sd-label">Price</div>
            <div className="sd-value">
              {state.price} {state.currency}
            </div>
          </div>
        </div>
      </div>
      <ItemOptions />
      <div className="item-options">
        <div className="option-field">
          <div className="option-title">Tracked:</div>
          <label class="checkbox-toggle">
            <input
              type="checkbox"
              id="tracked-checkbox"
              inputName="tracked"
              value={tracked}
              onChange={onChange}
            />
            <span class="slider round"></span>
          </label>
        </div>
      </div>
      <div className="sd-options">
        <Button value="Add to list" onClick={addItemToMyList} />
      </div>
    </div>
  );
};

export default ScrapedData;
